import React from 'react';
import Input from './Input';

class Forms extends React.Component {

  state = {
    nome: '',
    email: '',
    cpf: '',
    endereço: '',
    cidade: '',
    estado: '',
    tipo: '',
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    })
  }

  render() {
    return (
      <form>
        <Input name="nome" type="text" handler={ this.handleChange }/>
        <Input name="email" type="email" handler={ this.handleChange }/>
        <Input name="cpf" type="number" handler={ this.handleChange }/>
        <Input name="endereço" type="text" handler={ this.handleChange }/>
      </form>
    )
  }
}

export default Forms;
