import React from 'react';
import Input from './Input';

class Forms extends React.Component {

  state = {
    nome: '',
    email: '',
    cpf: '',
    endereço: '',
    cidade: '',
    estado: '',
    tipo: '',
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    })
  }

  render() {
    return (
      <form>
        <Input name="nome" length="40" description="Nome:" type="text" handler={ this.handleChange }/>
        <Input name="email" length="50" description="Email:" type="email" handler={ this.handleChange }/>
        <Input name="cpf" length="11" description="CPF:" type="text" handler={ this.handleChange }/>
        <Input name="endereço"length="200" description="Endereço:" type="text" handler={ this.handleChange }/>
      </form>
    )
  }
}

export default Forms;
