import React from 'react';

class Input extends React.Component {
  render() {
    const { handler, type, name, description, length, value } = this.props;;
    return (
      <label htmlFor={ name }> { description }
        <input value={ value } maxLength={ length } type={ type } onChange={ handler } name={ name }></input>
      </label>
    )
  }
}

export default Input;
